package com.String.demo;

public class StrinTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a="my name is gireesh";
		
		System.out.println("Length is  "+a.length());
		System.out.println("Trim "+a.trim().length());
		System.out.println("char at "+a.charAt(6));
		System.out.println("Index of "+a.indexOf('h'));
		System.out.println("Sub string "+a.substring(2, 6));
	}

}
